import { useState } from 'react';
import './App.css';

function App() {

  const [tasks, setTasks] = useState([]);        // stores all tasks
  const [newTask, setNewTask] = useState('');    // stores input value
  const [editIndex, setEditIndex] = useState(null); // stores index of task being edited


  // This function handles both adding and updating tasks
  function addTask() {

    if (newTask.trim() === '') return; // Don't add empty tasks

    if (editIndex !== null) {
      const updatedTasks = [...tasks];
      updatedTasks[editIndex] = newTask; // replace the task
      setTasks(updatedTasks);
      setEditIndex(null); // reset edit mode
    } 
    else {                          // Adding a new task
      setTasks([...tasks, newTask]);
    }

    setNewTask("");      // clear input after add/update
  }

  // Delete task at given index
  function deleteTask(index) {
    setTasks(tasks.filter((task, i) => i !== index));
  }

  // Set task to input and switch to edit mode
  function editTask(index) {
    setNewTask(tasks[index]);   // show selected task in input box
    setEditIndex(index);        // set edit mode
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1> Task Manager App </h1>

        <div>
          <input
            type="text"
            placeholder="Enter Task"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
          />

          <button onClick={addTask}>
            {editIndex !== null ? "Update Task" : "Add Task"} {/* change button text */}
          </button>
        </div>

        <div>
        {tasks.map((task, i) => (
  <div key={i}>
    <li style={{ textDecoration: task.completed ? 'line-through' : 'none' }}>
      {task.text}
    </li>
    <button onClick={() => toggleComplete(i)}>
      {task.completed ? "Undo" : "Complete"}
    </button>
    <button onClick={() => editTask(i)}>Edit</button>
    <button onClick={() => deleteTask(i)}>Delete</button>
  </div>
))}

        </div>
      </header>
    </div>
  );
}

export default App;
