import { useEffect, useState } from "react";
import { FaCheckCircle, FaEdit, FaTrash, FaUndoAlt } from "react-icons/fa";
import "./TaskPage.css";

function TaskPage() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [editIndex, setEditIndex] = useState(null);

  const apiURL = "http://localhost:5000/tasks";

  useEffect(() => {
    fetch(apiURL)
      .then((res) => res.json())
      .then((data) => setTasks(data));
  }, []);

  const handleAddOrUpdate = () => {
    if (newTask.trim() === "") return;

    const taskObject = { text: newTask, completed: false };

    if (editIndex !== null) {
      const updatedTasks = [...tasks];
      updatedTasks[editIndex].text = newTask;
      setTasks(updatedTasks);

      fetch(`${apiURL}/${editIndex}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(updatedTasks[editIndex]),
      });

      setEditIndex(null);
    } else {
      fetch(apiURL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(taskObject),
      }).then(() => setTasks([...tasks, taskObject]));
    }

    setNewTask("");
  };

  const handleDelete = (index) => {
    fetch(`${apiURL}/${index}`, { method: "DELETE" }).then(() => {
      const updated = tasks.filter((_, i) => i !== index);
      setTasks(updated);
    });
  };

  const handleEdit = (index) => {
    setNewTask(tasks[index].text);
    setEditIndex(index);
  };

  const toggleComplete = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].completed = !updatedTasks[index].completed;
    setTasks(updatedTasks);

    fetch(`${apiURL}/${index}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(updatedTasks[index]),
    });
  };

  return (
    <div className="task-container">
      <div className="task-card">
        <h2>Manage Your Tasks</h2>

        <div className="input-area">
          <input
            type="text"
            placeholder="Enter a task..."
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
          />
          <button onClick={handleAddOrUpdate}>
            {editIndex !== null ? "Update" : "Add"}
          </button>
        </div>

        <ul className="task-list">
          {tasks.map((task, i) => (
            <li key={i} className="task-item">
              <span className={task.completed ? "completed" : ""}>
                {task.text}
              </span>

              <div className="icon-group">
                <button onClick={() => toggleComplete(i)} title="Complete">
                  {task.completed ? (
                    <FaUndoAlt className="icon undo" />
                  ) : (
                    <FaCheckCircle className="icon complete" />
                  )}
                </button>
                <button onClick={() => handleEdit(i)} title="Edit">
                  <FaEdit className="icon edit" />
                </button>
                <button onClick={() => handleDelete(i)} title="Delete">
                  <FaTrash className="icon delete" />
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default TaskPage;
